mapper.select:
SELECT , GROUP_CONCAT(l.label) AS label FROM article a LEFT JOIN article_label_auth ala ON a.article_id = ala.article_id LEFT JOIN label l ON ala.label_id = l.label_id
AND a.title LIKE '%${title}%'
AND a.banner = #{banner}
AND a.is_private = #{isPrivate}
AND l.label LIKE '%${label}%'
GROUP BY a.article_id ORDER BY a.sort_no

mapper.update:
UPDATE article
title = #{title}, content = #{content}, banner = #{banner}, class_id = #{classId}, sort_no = #{sortNo}, is_private = #{isPrivate}, visited_number = #{visitedNumber}, support_number = #{supportNumber}, modify_user = #{modifyUser}, modify_time = NOW()
WHERE article_id = #{articleId}
