mapper.select:
SELECT

FROM `user` u


        AND u.login_name = #{loginName}


        AND u.`password` = #{password}


        AND u.login_type = #{loginType}


        AND u.`status` = #{status}

SELECT
FROM `user` u
AND u.login_type = #{loginType}
AND u.`status` = #{status}
AND (u.login_name LIKE '%${keyWord}%' OR u.real_name LIKE '%${keyWord}%' OR u.phone LIKE '%${keyWord}%' OR u.email LIKE '%${keyWord}%')

mapper.update:
UPDATE `user`
login_name = #{loginName}, real_name = #{realName}, `password` = #{password}, phone = #{phone}, email = #{email}, last_login_time = #{lastLoginTime}, login_type = #{loginType}, `status` = #{status}, modify_user = #{modifyUser}, modify_time = NOW()
WHERE user_id = #{userId}
